using System.Security.Cryptography.X509Certificates;
using Seatpicker.Application.Features.Login.Ports;

namespace Seatpicker.Infrastructure.Adapters.AuthCertificateProvider;

internal class FakeAuthCertificateProvider : IAuthCertificateProvider
{
    public Task<X509Certificate2> Get()
    {
        var bytes = Convert.FromBase64String(
            "");

        var certificate = new X509Certificate2(bytes, "asdasd");
        return Task.FromResult<X509Certificate2>(certificate);
    }
}